import React from 'react';

function Hero() {
  return (
    <div className="hero" style={Styles.hero}>
      <div className="photo" style={Styles.photo}>
        <img src={require("../assets/cece.JPG")} style={Styles.image}></img>
      </div>
      <div className="greeting" style={Styles.greeting}>
        <p>Hello, I'm Ca'Nese.</p>
        <p>I'm a web and mobile developer. I create POCs for fun!</p>
        <button>Read More</button>
      </div>
      {/* /* show a paragraph and change height for about page */ }
    </div>
  );
}

const Styles = {
  hero: {
    height: "40%",
    width: "100%",
    display: "flex",
    flexDirection: "row",
    border: "black 2px solid"
  },
  photo: {
    width: "30%",
    height: "100%"
  },
  image: {
    height: "15em",
    width: "15em",
    border: "#FF5F58 3px solid",
    borderRadius: "50%"
  },
  greeting: {
    display: "flex",
    flexDirection: "column",
    height: "auto",
    width: "70%"
  }
}

export default Hero;
