import React from 'react';

function Hero() {
  return (
    <div className="hero" style={Styles.hero}>
      <img src={require("../assets/cece.JPG")} style={Styles.image}></img>
      <div style={Styles.heroContainer}>

        <div className="greeting" style={Styles.greeting}>
          <p style={Styles.sentenceOne}>Hello, I'm Ca'Nese.</p>
          <p style={Styles.sentenceTwo}>I'm a web and mobile developer. I create POCs for fun!</p>
          <button style={{width: "8em"}}>Read More</button>
        </div>
        {/* /* show a paragraph and change height for about page */ }
      </div>

    </div>
  );
}

const Styles = {
  hero: {
    height: "35vh",
    width: "100vw",
    marginTop: "2vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    backgroundColor: "#E3E1D9",
    boxShadow: "0 2px 20px rgba(0, 0, 0, 0.08), 0 -2px 20px rgba(0, 0, 0, 0.08)"
  },
  heroContainer: {
    display: "flex",
    flexDirection: "row",
    width: "100vw",
    height: "auto",
    // border: "black 2px solid",
    paddingLeft: "15vw"
  },
  photo: {
    height: "100%",
    width: "30%",
    border: "red 2px solid",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center"
  },
  image: {
    height: "auto",
    maxWidth: "13vw",
    border: "#FF5F58 3px solid",
    borderRadius: "50%",
  },
  greeting: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    height: "100%",
    widht: "70%",
    border: "red 2px solid",
  },
  sentenceOne: {
    margin: 0,
    padding: 0,
    fontSize: "4vh"
  },
  sentenceTwo: {
    margin: 0,
    padding: 0,
    fontSize: "1.5em",
    marginBottom: "2vh"
  }
}

export default Hero;
