import React from 'react';

function Hero() {
  return (
    <div className="hero" style={Styles.hero}>
      <div style={Styles.heroContainer}>
        <div className="photo" style={Styles.photo}>
        <img src={require("../assets/cece.JPG")} style={Styles.image}></img>
      </div>
      <div className="greeting" style={Styles.greeting}>
        <p style={Styles.sentenceOne}>Hello, I'm Ca'Nese.</p>
        <p style={Styles.sentenceTwo}>I'm a web and mobile developer. I create POCs for fun!</p>
        <button style={{width: "8em"}}>Read More</button>
      </div>
      {/* /* show a paragraph and change height for about page */ }
      </div>

    </div>
  );
}

const Styles = {
  hero: {
    height: "40%",
    width: "100%",

    border: "black 2px solid",
    paddingTop: "4em"
  },
  heroContainer: {
    display: "flex",
    flexDirection: "row",
  },
  photo: {
    width: "30%",
    height: "100%"
  },
  image: {
    height: "13em",
    width: "13em",
    border: "#FF5F58 3px solid",
    borderRadius: "50%"
  },
  greeting: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    height: "auto",
    width: "70%",
    marginTop: "3em"
  },
  sentenceOne: {
    margin: 0,
    padding: 0,
    fontSize: "2em"
  },
  sentenceTwo: {
    margin: 0,
    padding: 0,
    fontSize: "1.5em",
    marginBottom: "2em"
  }
}

export default Hero;
